package diip_java_cc.Model;

import java.awt.image.BufferedImage;
import java.io.File;
import java.io.IOException;
import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;
import java.net.SocketException;
import java.net.UnknownHostException;
import java.util.ArrayList;
import java.util.List;
import java.util.Observable;
import java.util.concurrent.TimeUnit;

import javax.imageio.ImageIO;

public class Model extends Observable {

	// ================================================================================
	// Public Data
	// ================================================================================


	// ================================================================================
	// Private Data
	// ================================================================================
	BufferedImage sourceImage = null;
	BufferedImage outputImage = null;
	
	private int availableWorkers = 0;

	private LocalWorker localWorker1 = null;
	private LocalWorker localWorker2 = null;
	
	// ================================================================================
	// Private Functions
	// ================================================================================

	/**
	 * Counts the number of available workers
	 */
	private void assessWorkers() {
		// local workers
		availableWorkers = 0;
		if(localWorker1!=null) availableWorkers++;
		if(localWorker2!=null) availableWorkers++;
//		System.out.printf("Workers: %d\n",availableWorkers);
		// notify to update GUI
		this.setChanged();
		this.notifyObservers();
	}

	// ================================================================================
	// Public Functions
	// ================================================================================
	public Model() {
	}
	
	public void loadSourceImage(String fname) {
		
		File fimage = new File(fname);
		
		try {
			sourceImage = ImageIO.read(fimage);
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		
		this.setChanged();
		this.notifyObservers();
	}

	public BufferedImage getSourceImage() {
		return sourceImage;
	}

	public BufferedImage getOutputImage() {
		return outputImage;
	}

	public void localWorkerSetEnabled(int i, boolean selected) {
		if(selected) {
			if(i==1) {
				localWorker1 = new LocalWorker();
				localWorker1.start();
			}
			if(i==2) {
				localWorker2 = new LocalWorker();
				localWorker2.start();
			}
		}
		else {
			if(i==1) {
				localWorker1.terminate();
				localWorker1 = null;
			}
			if(i==2) {
				localWorker2.terminate();
				localWorker2 = null;
			}
		}
		this.assessWorkers();
	}
	
	public int getNWorkers() {
		return availableWorkers;
	}
	
	


	// ================================================================================
	// Test Functions
	// ================================================================================
	public void udpTest() {
		DatagramSocket socket = null;
		InetAddress address = null;
		
		LocalWorker lw = new LocalWorker();
		lw.start();
		
		// Create new socket
		try {
			socket = new DatagramSocket();
		} catch (SocketException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		
		// Set destination
		try {
			address = InetAddress.getByName("localhost");
		} catch (UnknownHostException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		
		// Message to send
		String msg = "Hello World";
		
		byte[] buf = msg.getBytes();
        DatagramPacket packet = new DatagramPacket(buf, buf.length, address, lw.getPort());
        try {
			socket.send(packet);
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
        packet = new DatagramPacket(buf, buf.length);
        try {
			socket.receive(packet);
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
        String received = new String(packet.getData(), 0, packet.getLength());
        System.out.printf("Received: %s\n", received);
		
		// Message to send
		msg = "end";
		
		buf = msg.getBytes();
        packet = new DatagramPacket(buf, buf.length, address, lw.getPort());
        try {
			socket.send(packet);
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
        packet = new DatagramPacket(buf, buf.length);
        try {
			socket.receive(packet);
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
        received = new String(packet.getData(), 0, packet.getLength());
        System.out.printf("Received: %s\n", received);
		
        lw.terminate();
	}
	

	public void uftTest() {
		DatagramSocket socket = null;
		InetAddress address = null;
		
		LocalWorker lw = new LocalWorker();
		lw.start();
		
		// Create new socket
		try {
			socket = new DatagramSocket();
		} catch (SocketException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		
		// Set destination
		try {
			address = InetAddress.getByName("localhost");
//			address = InetAddress.getByName("192.168.0.2");
		} catch (UnknownHostException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		
		// Message to send
		UFTData udata = new UFTData();
		udata.tcid = 42;

		UFT.setUserReg(0, 12000, socket, address, lw.getPort());
		UFT.setUserReg(1, 1, socket, address, lw.getPort());
		UFT.setUserReg(2, 13057284, socket, address, lw.getPort());
		UFT.setUserReg(3, 3, socket, address, lw.getPort());
		UFT.setUserReg(4, 15, socket, address, lw.getPort());
		UFT.setUserReg(5, 1024, socket, address, lw.getPort());
		UFT.setUserReg(6, 65536, socket, address, lw.getPort());
		UFT.setUserReg(7, 65535, socket, address, lw.getPort());

		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		
        try {
			TimeUnit.SECONDS.sleep(1);
		} catch (InterruptedException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}

        System.out.printf("Done\n");
        lw.terminate();
	}


	
}
