package diip_java_cc.Model;

import java.awt.image.BufferedImage;
import java.io.File;
import java.io.IOException;
import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;
import java.net.SocketException;
import java.net.UnknownHostException;
import java.util.ArrayList;
import java.util.List;
import java.util.Observable;
import java.util.concurrent.TimeUnit;

import javax.imageio.ImageIO;

public class Model extends Observable {

	// ================================================================================
	// Public Data
	// ================================================================================


	// ================================================================================
	// Private Data
	// ================================================================================
	private List<BufferedImage> inputImages = new ArrayList<BufferedImage>();
	BufferedImage sourceImage = null;
	BufferedImage outputImage = null;
	
	private int availableWorkers = 0;

	private LocalWorker localWorker1 = null;
	private LocalWorker localWorker2 = null;
	
	private DistributedProcessor dp;
	private List<Worker> workers = new ArrayList<Worker>();
	private Worker fpgaWorker1;
	private Worker fpgaWorker2;
	
	private long starttime = 0;
	private long endtime = 0;
	private double throughput = 0;
	
	// ================================================================================
	// Private Functions
	// ================================================================================

	/**
	 * Counts the number of available workers
	 */
	private void assessWorkers() {
		// local workers
		availableWorkers = 0;
		if(localWorker1!=null) availableWorkers++;
		if(localWorker2!=null) availableWorkers++;
		if(fpgaWorker1!=null) availableWorkers++;
		if(fpgaWorker2!=null) availableWorkers++;
//		System.out.printf("Workers: %d\n",availableWorkers);
		// notify to update GUI
		this.setChanged();
		this.notifyObservers();
	}

	// ================================================================================
	// Public Functions
	// ================================================================================
	public Model() {
		dp = new DistributedProcessor(this);
	}
	
	public void loadSourceImage(String fname) {
		
		File fimage = new File(fname);
		
		try {
			sourceImage = ImageIO.read(fimage);
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		
		this.setChanged();
		this.notifyObservers();
	}
	
	public void newInputFile(File file) {
		try {
			sourceImage = ImageIO.read(file);
		} catch (Exception e) {
			System.err.println("FATAL: Error read file");
		}
		
		this.setChanged();
		this.notifyObservers();
}

	public BufferedImage getSourceImage() {
		return sourceImage;
	}

	public BufferedImage getOutputImage() {
		return outputImage;
	}

	public void localWorkerSetEnabled(int i, boolean selected) {
		if(selected) {
			if(i==1) {
				localWorker1 = new LocalWorker(2230);
				localWorker1.start();
			}
			if(i==2) {
				localWorker2 = new LocalWorker(2231);
				localWorker2.start();
			}
		}
		else {
			if(i==1) {
				localWorker1.terminate();
				localWorker1 = null;
			}
			if(i==2) {
				localWorker2.terminate();
				localWorker2 = null;
			}
		}
		this.assessWorkers();
	}
	
	public int getNWorkers() {
		return availableWorkers;
	}
	


	public void goRequest(WallisParameters waparin) {
		if(!dp.isRunning()) {
			dp = new DistributedProcessor(this);
			// Assemble list of workers
			workers = new ArrayList<Worker>();
			if(localWorker1!=null) {
				workers.add(new Worker("localhost",localWorker1.getPort(),2230));
			}
			if(localWorker2!=null) {
				workers.add(new Worker("localhost",localWorker2.getPort(),2231));
			}
			if(fpgaWorker1 != null) {
				workers.add(new Worker(fpgaWorker1.ip, fpgaWorker1.port,2222));
			}
			if(fpgaWorker2 != null) {
				workers.add(new Worker(fpgaWorker2.ip, fpgaWorker2.port,2221));
			}
			dp.setWorkers(workers);
			inputImages.clear();
			inputImages.add(sourceImage);
			dp.setImagess(inputImages);

			WallisParameters wapar = waparin;
//	    	wapar.brightness = 0.5;
//	    	wapar.contrast = 0.8125;
//	    	wapar.gMean = 127;
//	    	wapar.gVar = 3600;
//	    	wapar.winLen = 21;
	    	
	    	dp.setWaPar(wapar);
			dp.start();
			starttime = System.nanoTime();
		}
		
		setChanged();
		notifyObservers();
	}

	
	public DistributedProcessor getDP() { 
		return dp;
	}
	
	public double getThroughput() {
		return throughput;
	}

	public void notifyDpDone() {
		endtime = System.nanoTime();
		throughput = (sourceImage.getHeight()*sourceImage.getWidth()/((endtime-starttime) / 1.0e9) / 1024 / 1024);
		outputImage = dp.getOutImages().get(0);
		setChanged();
		notifyObservers();
	}

	public void fpgaChanged(int i, boolean selected, String ipport) {
		String [] arrOfStr = ipport.split(":", 3);
		
		if(selected) System.out.printf("%d 1\n",i);		
		else System.out.printf("%d 0\n",i);
		
		if(selected) {
			if(i == 1)
				fpgaWorker1 = new Worker(arrOfStr[0], Integer.parseInt(arrOfStr[1]), Integer.parseInt(arrOfStr[2]));
			if(i == 2)
				fpgaWorker2 = new Worker(arrOfStr[0], Integer.parseInt(arrOfStr[1]), Integer.parseInt(arrOfStr[2]));
		}
		else {
			if(i == 1)
				fpgaWorker1 = null;
			if(i == 2)
				fpgaWorker2 = null;
		}
		this.assessWorkers();
	}

	// ================================================================================
	// Test Functions
	// ================================================================================
	public void udpTest() {
		DatagramSocket socket = null;
		InetAddress address = null;
		
		LocalWorker lw = new LocalWorker(2222);
		lw.start();
		
		// Create new socket
		try {
			socket = new DatagramSocket();
		} catch (SocketException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		
		// Set destination
		try {
			address = InetAddress.getByName("localhost");
		} catch (UnknownHostException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		
		// Message to send
		String msg = "Hello World";
		
		byte[] buf = msg.getBytes();
        DatagramPacket packet = new DatagramPacket(buf, buf.length, address, lw.getPort());
        try {
			socket.send(packet);
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
        packet = new DatagramPacket(buf, buf.length);
        try {
			socket.receive(packet);
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
        String received = new String(packet.getData(), 0, packet.getLength());
        System.out.printf("Received: %s\n", received);
		
		// Message to send
		msg = "end";
		
		buf = msg.getBytes();
        packet = new DatagramPacket(buf, buf.length, address, lw.getPort());
        try {
			socket.send(packet);
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
        packet = new DatagramPacket(buf, buf.length);
        try {
			socket.receive(packet);
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
        received = new String(packet.getData(), 0, packet.getLength());
        System.out.printf("Received: %s\n", received);
		
        lw.terminate();
	}
	

	public void uftTest() {
		DatagramSocket socket = null;
		InetAddress address = null;
		
		LocalWorker lw = new LocalWorker(2222);
		lw.start();
		
		// Create new socket
		try {
			socket = new DatagramSocket();
		} catch (SocketException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		
		// Set destination
		try {
			address = InetAddress.getByName("localhost");
//			address = InetAddress.getByName("192.168.0.2");
		} catch (UnknownHostException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		
		// Message to send
		UFTData udata = new UFTData();
		udata.tcid = 42;

		UFT.setUserReg(0, 12000, socket, address, lw.getPort());
		UFT.setUserReg(1, 1, socket, address, lw.getPort());
		UFT.setUserReg(2, 13057284, socket, address, lw.getPort());
		UFT.setUserReg(3, 3, socket, address, lw.getPort());
		UFT.setUserReg(4, 15, socket, address, lw.getPort());
		UFT.setUserReg(5, 1024, socket, address, lw.getPort());
		UFT.setUserReg(6, 65536, socket, address, lw.getPort());
		UFT.setUserReg(7, 65535, socket, address, lw.getPort());

		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		udata.data = "".getBytes();
		UFT.send(udata, socket, address, lw.getPort());
		
        try {
			TimeUnit.SECONDS.sleep(1);
		} catch (InterruptedException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}

        System.out.printf("Done\n");
        lw.terminate();
	}



	
}
